import org.apache.commons.text.StringEscapeUtils; import java.nio.file.Paths; import java.io.File; import java.util.Scanner; public class SupportPortalServlet { public void doGet(HttpServletRequest request, HttpServletResponse response) throws IOException { String name = request.getParameter("name"); name = StringEscapeUtils.escapeHtml4(name); response.getWriter().println("<h1>Hello " + name + "</h1>"); String filename = Paths.get(request.getParameter("file")).getFileName().toString(); File file = new File("/uploads/" + filename); Scanner scanner = new Scanner(file); while(scanner.hasNextLine()) { response.getWriter().println(scanner.nextLine()); } log.info("User login attempted for user: {}", request.getParameter("username")); } }